import yaml
from pydantic import BaseModel, StrictStr, StrictBool, validator
from pathlib import Path

class AppLogConfig(BaseModel):
    level: StrictStr
    file: Path
    max_bytes: int
    backup_count: int

class AppConfig(BaseModel):
    name: StrictStr
    log: AppLogConfig
    quantumdelayms: int
    triggerdelayms: int
    scanintervalms: int

class IdentityConfig(BaseModel):
    namespace: StrictStr
    version: StrictStr

class PluginConfig(BaseModel):
    path: Path
    enabled: StrictBool

class SensorConfig(BaseModel):
    eeg: StrictBool
    vr: StrictBool
    environmental: StrictBool

class PrometheusConfig(BaseModel):
    host: StrictStr
    port: int

class MetricsConfig(BaseModel):
    prometheus: PrometheusConfig

class Config(BaseModel):
    app: AppConfig
    identity: IdentityConfig
    plugins: PluginConfig
    sensors: SensorConfig
    metrics: MetricsConfig

    @validator('app')
    def check_intervals(cls, v):
        if v.scanintervalms < 0:
            raise ValueError("scanintervalms deve ser nÃ£o-negativo")
        return v

def load_config(path: Path = Path("config.yaml")) -> Config:
    with open(path, "r", encoding="utf-8") as f:
        raw = yaml.safe_load(f)
    return Config(**raw)
