import importlib.util
import logging
from pathlib import Path
from typing import Callable, List
from asyncio import iscoroutinefunction

logger = logging.getLogger(__name__)

class PluginManager:
    def __init__(self, plugin_path: Path, enabled: bool):
        self.path = plugin_path
        self.enabled = enabled
        self.plugins: List[Callable] = []
        if self.enabled:
            self.load_plugins()

    def load_plugins(self):
        if not self.path.exists() or not self.path.is_dir():
            logger.warning(f"Pasta de plugins nÃ£o encontrada: {self.path}")
            return

        for file in self.path.glob("*.py"):
            spec = importlib.util.spec_from_file_location(file.stem, file)
            module = importlib.util.module_from_spec(spec)
            spec.loader.exec_module(module)
            hook = getattr(module, "on_event", None)
            if callable(hook) or iscoroutinefunction(hook):
                self.plugins.append(hook)
                logger.info(f"Plugin carregado: {file.stem}")

    async def execute_hooks(self, event) -> None:
        for hook in self.plugins:
            try:
                if iscoroutinefunction(hook):
                    await hook(event)
                else:
                    hook(event)
            except Exception as e:
                logger.error(f"Erro no plugin {hook.__module__}: {e}")
