import logging
import hashlib
import hmac
from datetime import datetime
from typing import Dict, Any
from .identity import Identity
from .config import Config

logger = logging.getLogger(__name__)

class LuxCastProtocol:
    def __init__(self, cfg: Config, identity: Identity):
        self.cfg = cfg
        self.identity = identity
        self.akashic_key = self._generate_akashic_key()

    def _generate_akashic_key(self) -> str:
        """Gera chave quÃ¢ntica baseada na identidade vibracional"""
        seed = f"{self.identity.namespace}-{datetime.utcnow().timestamp()}"
        return hmac.new(
            self.identity.signature.encode(),
            seed.encode(),
            hashlib.sha512
        ).hexdigest()

    def encode_message(self, intention: str, content: str, frequency: float = 432.0) -> Dict[str, Any]:
        """Estrutura de mensagem LuxCast v2"""
        return {
            "metadata": {
                "timestamp": datetime.utcnow().isoformat(),
                "source": self.identity.lux_id(),
                "frequency": frequency,
                "intention": intention,
                "akashic_ref": self.akashic_key[:16]
            },
            "content": content,
            "signature": self._sign_message(content)
        }

    def _sign_message(self, content: str) -> str:
        """Assinatura vibracional LuxSeal"""
        return hmac.new(
            self.akashic_key.encode(),
            content.encode(),
            hashlib.sha256
        ).hexdigest()

    def validate_message(self, message: Dict[str, Any]) -> bool:
        """ValidaÃ§Ã£o de consentimento vibracional"""
        try:
            content = message["content"]
            claimed_signature = message["signature"]
            actual_signature = self._sign_message(content)
            
            # VerificaÃ§Ã£o de coerÃªncia temporal (janela de 5s)
            msg_time = datetime.fromisoformat(message["metadata"]["timestamp"])
            time_diff = (datetime.utcnow() - msg_time).total_seconds()
            
            return hmac.compare_digest(claimed_signature, actual_signature) and abs(time_diff) < 5.0
        except Exception as e:
            logger.error(f"Falha na validaÃ§Ã£o LuxCast: {e}")
            return False
