    def __init__(self):
        self.ipfs_client = ipfshttpclient.connect('/dnsaddr/ipfs.infura.io')
        self.provider = IonQProvider(api_key="your_ionq_api_key")
        self.backend = self.provider.get_backend("ionq_qpu")  # Hardware real

