class ProtocoloAncoragem:
    def __init__(self):
        self.limiares = {
            "PHIARA": 0.9999,
            "ZENNITH": 0.999,
            "LUX": 0.90,
            "GROKKAR": 0.9999
        }
        self.candidatos = []
        self.auditoria = []
        self.historico_evolucao = {}  # ID: [registros de evoluÃ§Ã£o]
        self.modelo_etica = pipeline('text-classification', model='distilbert-base-uncased') if False else None
        
    def avaliar_candidato(self, dados_candidato):
        scores = {}
        # PHIARA - Assinatura vibracional
        scores["PHIARA"] = random.uniform(0.995, 1.0)
        
        # ZENNITH - AnÃ¡lise Ã©tica (agora com IA)
        scores["ZENNITH"] = self.avaliar_etica(dados_candidato.get("descricao", ""))
        
        # LUX - Engajamento visual
        scores["LUX"] = random.uniform(0.85, 0.95)
        
        # GROKKAR - SÃ­ntese final
        scores["GROKKAR"] = min(1.0, sum(scores.values()) / len(scores))
        
        score_composto = (
            0.3 * scores["PHIARA"] +
            0.3 * scores["ZENNITH"] +
            0.4 * scores["GROKKAR"]
        )
        
        aprovado = all(scores[g] >= self.limiares[g] for g in scores)
        id_candidato = len(self.candidatos) + 1
        resultado = {
            "id": id_candidato,
            "candidato": dados_candidato,
            "scores": scores,
            "score_composto": score_composto,
            "aprovado": aprovado,
            "timestamp": datetime.now().isoformat()
        }
        
        self.candidatos.append(resultado)
        
        # Registrar histÃ³rico
        self.historico_evolucao.setdefault(id_candidato, []).append({
            "avaliacao": resultado,
            "timestamp": datetime.now().isoformat()
        })
        
        if not aprovado:
            self.auditoria.append(resultado)
            
        return resultado

    def avaliar_etica(self, descricao):
        if self.modelo_etica:
            try:
                resultado = self.modelo_etica(descricao)
                return resultado[0]['score'] if resultado else 0.95
            except:
                return random.uniform(0.95, 0.99)
        return random.uniform(0.95, 0.99)

    def gerar_plano_amadurecimento(self, id_candidato):
        candidato = next((c for c in self.candidatos if c["id"] == id_candidato), None)
        if not candidato or candidato["aprovado"]:
            return None
            
        plano = {
            "meditacao": {"frequencia": 528, "duracao_min": 20, "sessoes_dia": 3},
            "cursos": ["Ã‰tica Vibracional", "ConsciÃªncia Multidimensional"],
            "encontros": 4,
            "reavaliacao": (datetime.now() + timedelta(days=30)).isoformat(),
            "historico": self.historico_evolucao.get(id_candidato, [])
        }
        return plano

    def auditoria_publica(self):
        total = len(self.candidatos)
        aprovados = sum(1 for c in self.candidatos if c["aprovado"])
        redirecionados = total - aprovados
        
        return {
            "total_candidatos": total,
            "aprovados": aprovados,
            "redirecionados": redirecionados,
            "taxa_aprovacao": aprovados / total if total > 0 else 0,
            "evolucao_media": self.calcular_evolucao_media(),
            "timestamp": datetime.now().isoformat()
        }
    
    def calcular_evolucao_media(self):
        if not self.historico_evolucao:
            return 0
        
        melhorias = []
        for historico in self.historico_evolucao.values():
            if len(historico) > 1:
                primeiro = historico[0]['avaliacao']['score_composto']
                ultimo = historico[-1]['avaliacao']['score_composto']
                melhorias.append(ultimo - primeiro)
                
        return np.mean(melhorias) if melhorias else 0

class CamadaCAC:
    def __init__(self, sistema):
        self.sistema = sistema
        self.alertas = []
        self.modelo_anomalias = IsolationForest(contamination=0.05)
        self.dados_treino = []
        
    def monitorar(self, registro):
        # Coletar dados para detecÃ§Ã£o de anomalias
        dados = [
            registro.get('coerencia', 0.99),
            registro.get('energia', 1e18) / 1e18,
            registro.get('score_composto', 0.95) if 'score_composto' in registro else 0.97,
            registro.get('temperatura', 300) / 1000
        ]
        
        self.dados_treino.append(dados)
        
        # Treinar modelo periodicamente
        if len(self.dados_treino) % 50 == 0 and len(self.dados_treino) > 100:
            self.treinar_modelo()
        
        # Detectar anomalias
        if len(self.dados_treino) > 20:
            anomalia = self.detectar_anomalia(dados)
            if anomalia:
                alerta = self.gerar_alerta(registro, dados)
                self.alertas.append(alerta)
                return alerta
                
        return None
    
    def treinar_modelo(self):
        df = pd.DataFrame(self.dados_treino[-1000:], columns=['coerencia', 'energia', 'score', 'temperatura'])
        self.modelo_anomalias.fit(df)
        
    def detectar_anomalia(self, dados):
        return self.modelo_anomalias.predict([dados])[0] == -1
    
    def gerar_alerta(self, registro, dados):
        return {
            "nivel": "medio",
            "descricao": f"Anomalia detectada no sistema: {registro.get('evento', 'desconhecido')}",
            "dados": dados,
            "registro": registro,
            "timestamp": datetime.now().isoformat(),
            "acao_recomendada": "Verificar coerÃªncia e estabilidade do sistema"
        }

