from future import annotations
import threading, time
from typing import Optional
import plotly.graph_objects as go

class DashboardExporter:
    def init(self, engine, intervals: int, outpath: str):
        self.engine = engine
        self.intervals = intervals
        self.outpath = outpath
        self._running = False
        self._thr: Optional[threading.Thread] = None

    def start(self):
        if self._running: return
        self._running = True
        self.thr = threading.Thread(target=self.run, daemon=True).start()

    def stop(self):
        self._running = False

    def _run(self):
        while self._running:
            self.export_once()
            time.sleep(self.interval_s)

    def export_once(self):
        snap = self.engine.get_snapshot()
        xs, ys, texts, sizes, colors = [], [], [], [], []
        vis = snap.get("visual", {})
        sizemetric = vis.get("metricas_size", "coerencia")
        smin, smax = int(vis.get("sizemin", 25)), int(vis.get("sizemax", 80))
        idtoidx = {}
        for i, n in enumerate(snap["nodes"]):
            m = n["metrics"].get(size_metric, 0.5)
            size = smin + int((smax - smin) * max(0.0, min(1.0, m)))
            xs.append(n["x"]); ys.append(n["y"])
            texts.append(f'{n["label"]}<br>{n["metrics"]}')
            sizes.append(size); colors.append(n.get("color", "#3AAFA9"))
            idtoidx[n["id"]] = i

        edgex, edgey = [], []
        for e in snap["edges"]:
            s, t = idtoidx[e["source"]], idtoidx[e["target"]]
            edge_x += [xs[s], xs[t], None]
            edge_y += [ys[s], ys[t], None]

        fig = go.Figure()
