import threading, time
import plotly.graph_objects as go

class DashboardExporter:
    def init(self, engine, intervals: int, outpath: str):
        self.engine = engine
        self.intervals = intervals
        self.outpath = outpath
        self._running = False

    def start(self):
        if self._running: return
        self._running = True
        threading.Thread(target=self._loop, daemon=True).start()

    def stop(self):
        self._running = False

    def _loop(self):
        while self._running:
            self.export_once()
            time.sleep(self.interval_s)

    def export_once(self):
        snap = self.engine.get_snapshot()
        fig = go.Figure()
        # nÃ³s
        xs, ys, text = [], [], []
        for n in snap["nodes"]:
            xs.append(n["x"]); ys.append(n["y"])
            text.append(f"{n['label']}<br>{n['metrics']}")
        fig.add_trace(go.Scatter(x=xs, y=ys, mode="markers+text", text=text))
        fig.writehtml(self.outpath, include_plotlyjs="cdn")
