def generate(n: int = 5000, freq: str = "S", seed: int = 42, anomaly_rate: float = 0.01):
    rng = np.random.default_rng(seed)
    ts = pd.date_range("2025-01-01", periods=n, freq=freq)
    base = np.sin(np.linspace(0, 50, n)) * 2.0 + 10.0
    noise = rng.normal(0, 0.5, size=n)
    values = base + noise

    # Anomalias pontuais e por rajada
    anomalies = rng.choice(n, size=int(n * anomaly_rate), replace=False)
    values[anomalies] += rng.normal(6, 2.0, size=len(anomalies))

    # Pequenos shifts de regime
    for start in rng.choice(range(200, n-200, 200), size=3, replace=False):
        shift = rng.normal(1.5, 0.3)
        values[start:start+100] += shift

    df = pd.DataFrame({"timestamp": ts, "value": values})
    return df

