from __future__ import annotations
import json
import time
import hashlib
import logging
from dataclasses import dataclass, asdict
from typing import Dict, Any, List, Optional

@dataclass
class Vote:
    agent: str
    decision: str  # "approve", "reject", "abstain"
    confidence: float
    reason: str

@dataclass
class DecisionRecord:
    id: str
    event: Dict[str, Any]
    votes: List[Vote]
    outcome: str  # "approved" | "rejected"
    quorum: float
    threshold: float
    veto_triggered: bool
    ts: float
    prev_hash: str
    hash: str

class Ledger:
    """
    Registro imutÃ¡vel com hash-cadeia simples (estilo blockchain, sem P2P).
    """
    def __init__(self, logger: Optional[logging.Logger] = None):
        self.chain: List[DecisionRecord] = []
        self.logger = logger or logging.getLogger("Ledger")

    def append(self, rec: DecisionRecord):
        self.chain.append(rec)
        self.logger.info("Registro anexado: %s (%s)", rec.id, rec.outcome)

    def last_hash(self) -> str:
        return self.chain[-1].hash if self.chain else "GENESIS"

    def export(self) -> List[Dict[str, Any]]:
        return [asdict(r) for r in self.chain]

class Agent:
    """
    Agente base com polÃ­tica simples.
    role pode ser: "ethics", "risk", "ops" (exemplos).
    """
    def __init__(self, name: str, role: str, weight: float = 1.0, can_veto: bool = False, params: Optional[Dict[str, Any]] = None):
        self.name = name
        self.role = role
        self.weight = float(weight)
        self.can_veto = bool(can_veto)
        self.params = params or {}

    def evaluate(self, event: Dict[str, Any]) -> Vote:
        """
        PolÃ­tica exemplificativa:
        - ethics: veta se score_ensemble > ethics_max_score
        - risk: aprova mitigaÃ§Ã£o se score > risk_alert_threshold
        - ops: aprova se custo_op < ops_max_cost
        """
        score = float(event.get("score_ensemble", 0.0))
        et_max = float(self.params.get("ethics_max_score", 0.95))
        rk_thr = float(self.params.get("risk_alert_threshold", 0.8))
        op_cost = float(event.get("estimated_cost", 0.0))
        op_max = float(self.params.get("ops_max_cost", 10.0))

        if self.role == "ethics":
            if score >= et_max:
                return Vote(self.name, "reject", 0.9, f"Score {score:.2f} excede limite Ã©tico {et_max:.2f}")
            return Vote(self.name, "approve", 0.7, "Dentro de parÃ¢metros Ã©ticos")

        if self.role == "risk":
            if score >= rk_thr:
                return Vote(self.name, "approve", 0.8, "MitigaÃ§Ã£o recomendada")
            return Vote(self.name, "abstain", 0.5, "Risco moderado")

        if self.role == "ops":
            if op_cost <= op_max:
                return Vote(self.name, "approve", 0.7, f"Custo aceitÃ¡vel ({op_cost} â‰¤ {op_max})")
            return Vote(self.name, "reject", 0.7, f"Custo elevado ({op_cost} > {op_max})")

        return Vote(self.name, "abstain", 0.5, "Sem polÃ­tica definida")

class OrquestradorTriade:
    """
    Sistema multi-agente com consenso, quÃ³rum e veto.
    """
    def __init__(self, config: Dict[str, Any], logger: Optional[logging.Logger] = None):
        self.config = config
        self.logger = logger or logging.getLogger("OrquestradorTriade")
        self.threshold = float(config.get("threshold", 0.66))  # proporÃ§Ã£o de votos aprovados ponderados
        self.quorum = float(config.get("quorum", 0.6))         # proporÃ§Ã£o mÃ­nima de participaÃ§Ã£o
        self.veto_roles = set(config.get("veto_roles", ["ethics"]))
        self.agents: List[Agent] = []
        for a in config.get("agents", []):
            self.agents.append(
                Agent(
                    name=a["name"],
                    role=a["role"],
                    weight=a.get("weight", 1.0),
                    can_veto=a.get("can_veto", False),
                    params=a.get("params", {}),
                )
            )
        self.ledger = Ledger(logger=self.logger)

    @staticmethod
    def from_json(path: str) -> "OrquestradorTriade":
        with open(path, "r", encoding="utf-8") as f:
            cfg = json.load(f)
        return OrquestradorTriade(cfg)

    def _hash_record(self, payload: Dict[str, Any]) -> str:
        blob = json.dumps(payload, sort_keys=True).encode("utf-8")
        return hashlib.sha256(blob).hexdigest()

    def propose(self, event: Dict[str, Any]) -> DecisionRecord:
        # Coleta votos
        votes: List[Vote] = [agent.evaluate(event) for agent in self.agents]
        self.logger.debug("Votos: %s", [asdict(v) for v in votes])

        # CÃ¡lculo de quÃ³rum
        participation = sum(1 for v in votes if v.decision != "abstain") / max(len(votes), 1)
        has_quorum = participation >= self.quorum

        # Veto
        veto_triggered = any(
            (a.can_veto or a.role in self.veto_roles) and v.decision == "reject"
            for a, v in zip(self.agents, votes)
        )

        # Score de aprovaÃ§Ã£o ponderado
        total_weight = sum(a.weight for a in self.agents if True)
        approve_weight = sum(a.weight for a, v in zip(self.agents, votes) if v.decision == "approve")
        approve_ratio = approve_weight / max(total_weight, 1e-6)

        outcome = "approved" if (has_quorum and not veto_triggered and approve_ratio >= self.threshold) else "rejected"

        payload = {
            "event": event,
            "votes": [asdict(v) for v in votes],
            "outcome": outcome,
            "quorum": self.quorum,
            "threshold": self.threshold,
            "veto_triggered": veto_triggered,
            "ts": time.time(),
            "prev_hash": self.ledger.last_hash(),
        }
        rec_hash = self._hash_record(payload)
        rec = DecisionRecord(
            id=event.get("id", rec_hash[:12]),
            votes=votes,
            outcome=outcome,
            quorum=self.quorum,
            threshold=self.threshold,
            veto_triggered=veto_triggered,
            ts=payload["ts"],
            prev_hash=payload["prev_hash"],
            hash=rec_hash,
            event=event,
        )
        self.ledger.append(rec)
        return rec# core/camada_cac.py
from __future__ import annotations
import json
import hashlib
import logging
from dataclasses import dataclass
from typing import Callable, Dict, Optional, List, Any, Iterable

import numpy as np
import pandas as pd
from sklearn.ensemble import IsolationForest
from sklearn.preprocessing import StandardScaler

# DependÃªncias opcionais
try:
    from prophet import Prophet  # type: ignore
    _HAS_PROPHET = True
except Exception:
    _HAS_PROPHET = False

try:
    import tensorflow as tf  # type: ignore
    from tensorflow.keras import layers, models  # type: ignore
    _HAS_TF = True
except Exception:
    _HAS_TF = False


@dataclass
class CACAlert:
    index: int
    timestamp: pd.Timestamp
    value: float
    score_if: float
    score_prophet: Optional[float]
    score_lstm: Optional[float]
    score_ensemble: float
    threshold: float
    meta: Dict[str, Any]


class CamadaCAC:
    """
    Camada CAC = Camada de AnÃ¡lise e ConvergÃªncia (anomalias preditivas).
    Ensemble: IsolationForest (obrigatÃ³rio) + Prophet (opcional) + LSTM Autoencoder (opcional).
    CombinaÃ§Ã£o por pesos configurÃ¡veis. Hooks para on_alert e on_metric.
    """

    def __init__(
        self,
        config: Dict[str, Any],
        on_alert: Optional[Callable[[CACAlert], None]] = None,
        on_metric: Optional[Callable[[Dict[str, Any]], None]] = None,
        logger: Optional[logging.Logger] = None,
    ):
        self.config = config
        self.on_alert = on_alert
        self.on_metric = on_metric
        self.logger = logger or logging.getLogger("CamadaCAC")

        self.window_size = int(self.config.get("window_size", 64))
        self.threshold = float(self.config.get("threshold", 0.8))
        self.weights = self.config.get("weights", {"if": 0.6, "prophet": 0.2, "lstm": 0.2})
        self.enabled_models = self.config.get("enabled_models", {"if": True, "prophet": False, "lstm": False})

        # Modelos
        self.scaler = StandardScaler()
        self.iforest = IsolationForest(
            n_estimators=self.config.get("iforest", {}).get("n_estimators", 200),
            contamination=self.config.get("iforest", {}).get("contamination", "auto"),
            random_state=self.config.get("iforest", {}).get("random_state", 42),
        )

        self.prophet_model = None
        self.lstm_model = None

        # Buffers
        self.series_buffer: List[float] = []
        self.ts_buffer: List[pd.Timestamp] = []

        # Estado
        self.fitted = False

    @staticmethod
    def from_json(path: str, **kwargs) -> "CamadaCAC":
        with open(path, "r", encoding="utf-8") as f:
            cfg = json.load(f)
        return CamadaCAC(cfg, **kwargs)

    def _init_prophet(self):
        if not self.enabled_models.get("prophet", False):
            return
        if not _HAS_PROPHET:
            self.logger.warning("Prophet nÃ£o disponÃ­vel; desativando componente.")
            self.enabled_models["prophet"] = False
            return
        self.prophet_model = Prophet()

    def _init_lstm(self):
        if not self.enabled_models.get("lstm", False):
            return
        if not _HAS_TF:
            self.logger.warning("TensorFlow nÃ£o disponÃ­vel; desativando LSTM.")
            self.enabled_models["lstm"] = False
            return
        model = models.Sequential(
            [
                layers.Input(shape=(self.window_size, 1)),
                layers.LSTM(32, return_sequences=True),
                layers.LSTM(16, return_sequences=False),
                layers.RepeatVector(self.window_size),
                layers.LSTM(16, return_sequences=True),
                layers.LSTM(32, return_sequences=True),
                layers.TimeDistributed(layers.Dense(1)),
            ]
        )
        model.compile(optimizer="adam", loss="mae")
        self.lstm_model = model

    def fit(self, df: pd.DataFrame, value_col: str = "value", ts_col: str = "timestamp"):
        """
        Fit inicial com uma sÃ©rie temporal.
        Espera colunas: timestamp (datetime-like), value (float).
        """
        df = df.copy()
        df[ts_col] = pd.to_datetime(df[ts_col])
        df = df.sort_values(ts_col).reset_index(drop=True)

        values = df[value_col].astype(float).values
        timestamps = df[ts_col].values

        # Treino IsolationForest em janelas
        X = self._build_window_features(values, self.window_size)
        X_scaled = self.scaler.fit_transform(X)
        self.iforest.fit(X_scaled)

        # Prophet (opcional)
        self._init_prophet()
        if self.enabled_models.get("prophet", False):
            df_prophet = pd.DataFrame({"ds": df[ts_col], "y": df[value_col].values})
            self.prophet_model.fit(df_prophet)

        # LSTM (opcional)
        self._init_lstm()
        if self.enabled_models.get("lstm", False):
            X_seq = self._build_sequences(values, self.window_size)
            self.lstm_model.fit(X_seq, X_seq, epochs=self.config.get("lstm", {}).get("epochs", 8), verbose=0, batch_size=32)

        # Preencher buffers com Ãºltimos pontos
        self.series_buffer = list(values[-self.window_size :])
        self.ts_buffer = list(pd.to_datetime(df[ts_col].iloc[-self.window_size :]))

        self.fitted = True
        self.logger.info("CamadaCAC treinada com %d pontos.", len(values))

    def _build_window_features(self, arr: np.ndarray, w: int) -> np.ndarray:
        if len(arr) < w:
            raise ValueError("SÃ©rie curta para janela solicitada.")
        feats = []
        for i in range(w, len(arr)):
            window = arr[i - w : i]
            # Features simples: estatÃ­sticas + Ãºltima variaÃ§Ã£o
            mu = window.mean()
            sigma = window.std() if window.std() > 1e-12 else 1e-12
            last = window[-1]
            d1 = window[-1] - window[-2] if w >= 2 else 0.0
            feats.append([mu, sigma, last, d1])
        return np.array(feats)

    def _build_sequences(self, arr: np.ndarray, w: int) -> np.ndarray:
        seqs = []
        for i in range(w, len(arr)):
            seq = arr[i - w : i]
            seqs.append(seq.reshape((w, 1)))
        return np.array(seqs, dtype=np.float32)

    def _score_iforest(self, window_values: np.ndarray) -> float:
        feats = []
        mu = window_values.mean()
        sigma = window_values.std() if window_values.std() > 1e-12 else 1e-12
        last = window_values[-1]
        d1 = window_values[-1] - window_values[-2] if len(window_values) >= 2 else 0.0
        feats.append([mu, sigma, last, d1])
        X = self.scaler.transform(np.array(feats))
        # decision_function: valores maiores = mais normal. Convertendo para score de anomalia [0,1]
        dec = self.iforest.decision_function(X)[0]  # ~[-0.5, 0.5]
        # normalizar para [0,1] invertendo (0 = normal, 1 = anÃ´malo)
        score = 1.0 - (dec - (-0.5)) / (0.5 - (-0.5))
        return float(np.clip(score, 0.0, 1.0))

    def _score_prophet(self, window_ts: List[pd.Timestamp], window_values: np.ndarray) -> Optional[float]:
        if not self.enabled_models.get("prophet", False) or self.prophet_model is None:
            return None
        # Prever prÃ³ximo ponto e medir resÃ­duo relativo
        future = pd.DataFrame({"ds": [window_ts[-1] + (window_ts[-1] - window_ts[-2])]})
        forecast = self.prophet_model.predict(future)
        yhat = float(forecast["yhat"].iloc[0])
        y = float(window_values[-1])
        resid = abs(y - yhat)
        denom = max(np.std(window_values), 1e-6)
        score = float(np.clip(resid / (3 * denom), 0.0, 1.0))
        return score

    def _score_lstm(self, window_values: np.ndarray) -> Optional[float]:
        if not self.enabled_models.get("lstm", False) or self.lstm_model is None:
            return None
        seq = window_values.reshape((1, self.window_size, 1)).astype(np.float32)
        recon = self.lstm_model.predict(seq, verbose=0)
        err = float(np.mean(np.abs(recon - seq)))
        # Normalizar erro por desvio padrÃ£o da janela
        denom = max(np.std(window_values), 1e-6)
        score = float(np.clip(err / (3 * denom), 0.0, 1.0))
        return score

    def step(self, timestamp: pd.Timestamp, value: float) -> Optional[CACAlert]:
        """
        Processa um novo ponto de streaming e, se ultrapassar o threshold de ensemble, emite alerta.
        """
        if not self.fitted:
            raise RuntimeError("CamadaCAC nÃ£o treinada. Chame fit() antes de step().")

        self.series_buffer.append(value)
        self.ts_buffer.append(pd.to_datetime(timestamp))

        if len(self.series_buffer) < self.window_size:
            return None

        # Manter janela
        self.series_buffer = self.series_buffer[-self.window_size :]
        self.ts_buffer = self.ts_buffer[-self.window_size :]

        window = np.array(self.series_buffer, dtype=float)
        score_if = self._score_iforest(window)
        score_prophet = self._score_prophet(self.ts_buffer, window)
        score_lstm = self._score_lstm(window)

        # Ensemble
        w_if = self.weights.get("if", 0.6) if score_if is not None else 0.0
        w_pr = self.weights.get("prophet", 0.2) if score_prophet is not None else 0.0
        w_ls = self.weights.get("lstm", 0.2) if score_lstm is not None else 0.0
        w_sum = max(w_if + w_pr + w_ls, 1e-6)

        parts = [
            (score_if if score_if is not None else 0.0) * w_if,
            (score_prophet if score_prophet is not None else 0.0) * w_pr,
            (score_lstm if score_lstm is not None else 0.0) * w_ls,
        ]
        score_ensemble = float(np.clip(sum(parts) / w_sum, 0.0, 1.0))

        alert: Optional[CACAlert] = None
        if score_ensemble >= self.threshold:
            alert = CACAlert(
                index=len(self.ts_buffer) - 1,
                timestamp=self.ts_buffer[-1],
                value=float(window[-1]),
                score_if=score_if,
                score_prophet=score_prophet,
                score_lstm=score_lstm,
                score_ensemble=score_ensemble,
                threshold=self.threshold,
                meta={
                    "weights": dict(if_=w_if, prophet=w_pr, lstm=w_ls),
                    "window_size": self.window_size,
                },
            )
            if self.on_alert:
                self.on_alert(alert)

        if self.on_metric:
            self.on_metric(
                {
                    "timestamp": str(self.ts_buffer[-1]),
                    "value": float(window[-1]),
                    "score_if": score_if,
                    "score_prophet": score_prophet,
                    "score_lstm": score_lstm,
                    "score_ensemble": score_ensemble,
                    "threshold": self.threshold,
                }
            )

        return alert

    def process_stream(self, rows: Iterable[Dict[str, Any]], ts_key="timestamp", value_key="value") -> Iterable[CACAlert]:
        for row in rows:
            ts = pd.to_datetime(row[ts_key])
            val = float(row[value_key])
            alert = self.step(ts, val)
            if alert:
                yield alert

