def json_formatter(record: logging.LogRecord) -> str:
    payload = {
        "ts": datetime.utcnow().isoformat() + "Z",
        "level": record.levelname,
        "logger": record.name,
        "message": record.getMessage(),
    }
    if record.args:
        payload["args"] = record.args
    return json.dumps(payload, ensure_ascii=False)

class JsonLogHandler(logging.StreamHandler):
    def emit(self, record):
        try:
            msg = json_formatter(record)
            stream = self.stream
            stream.write(msg + "\n")
            self.flush()
        except Exception:
            self.handleError(record)

def setup_logging():
    root = logging.getLogger()
    root.setLevel(logging.INFO)
    # Console JSON
    h = JsonLogHandler(sys.stdout)
    root.addHandler(h)
    # Audit (arquivo)
    os.makedirs(os.path.dirname(cfg.audit_log_path), exist_ok=True)
    audit_handler = logging.FileHandler(cfg.audit_log_path, encoding="utf-8")
    audit_handler.setLevel(logging.INFO)
    audit_handler.setFormatter(logging.Formatter('%(message)s'))  # jÃ¡ vem como JSON pronto
    audit_logger = logging.getLogger("audit")
    audit_logger.addHandler(audit_handler)
    audit_logger.propagate = False

