async def worker(session, idx):
    values = (10 + np.sin(np.arange(60)/6) + np.random.normal(0,0.2,60)).tolist()
    headers = {}
    if JWT: headers["Authorization"] = f"Bearer {JWT}"
    t0 = time.perf_counter()
    r = await session.post(f"{API}/decide", json={"values": values}, headers=headers, timeout=10.0)
    dt = time.perf_counter() - t0
    return {"status": r.status_code, "lat": dt}

async def main():
    results = []
    async with httpx.AsyncClient() as session:
        sem = asyncio.Semaphore(CONC)
        async def bound(i):
            async with sem:
                return await worker(session, i)
        tasks = [asyncio.create_task(bound(i)) for i in range(REQS)]
        for t in tasks:
            results.append(await t)
    lat = [r["lat"] for r in results if r["status"] == 200]
    out = {
        "sent": REQS,
        "ok": sum(1 for r in results if r["status"] == 200),
        "p50": float(np.percentile(lat, 50)) if lat else None,
        "p90": float(np.percentile(lat, 90)) if lat else None,
        "p99": float(np.percentile(lat, 99)) if lat else None,
    }
    print(json.dumps(out, indent=2))

